import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 64.2-2723 Disposition of unappointed property under released or unexercised nongeneral power.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-2723.md","filePath":"vacode/64.2-2723.md"}'),n={name:"vacode/64.2-2723.md"},i=t('<h1 id="§-64-2-2723-disposition-of-unappointed-property-under-released-or-unexercised-nongeneral-power" tabindex="-1">§ 64.2-2723 Disposition of unappointed property under released or unexercised nongeneral power. <a class="header-anchor" href="#§-64-2-2723-disposition-of-unappointed-property-under-released-or-unexercised-nongeneral-power" aria-label="Permalink to &quot;§ 64.2-2723 Disposition of unappointed property under released or unexercised nongeneral power.&quot;">​</a></h1><p>To the extent that a powerholder releases, ineffectively exercises, or fails to exercise a nongeneral power of appointment:</p><ol><li><p>The gift-in-default clause controls the disposition of the unappointed property; or</p></li><li><p>If there is no gift-in-default clause or to the extent that the clause is ineffective, the unappointed property:</p></li></ol><p>a. Passes to the permissible appointees if:</p><p>(1) The permissible appointees are defined and limited; and</p><p>(2) The terms of the instrument creating the power do not manifest a contrary intent; or</p><p>b. If there is no taker under subdivision 2 a, passes under a reversionary interest to the donor or the donor&#39;s transferee or successor in interest.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>.</p>',8),a=[i];function s(p,d,l,c,f,u){return o(),r("div",null,a)}const m=e(n,[["render",s]]);export{_ as __pageData,m as default};
