import{_ as t,o as e,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 64.2-308.4 Composition of the augmented estate; marital property portion.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-308.4.md","filePath":"vacode/64.2-308.4.md"}'),d={name:"vacode/64.2-308.4.md"},o=a('<h1 id="§-64-2-308-4-composition-of-the-augmented-estate-marital-property-portion" tabindex="-1">§ 64.2-308.4 Composition of the augmented estate; marital property portion. <a class="header-anchor" href="#§-64-2-308-4-composition-of-the-augmented-estate-marital-property-portion" aria-label="Permalink to &quot;§ 64.2-308.4 Composition of the augmented estate; marital property portion.&quot;">​</a></h1><p>A. Subject to § <a href="http://law.lis.virginia.gov/vacode/64.2-308.9/" target="_blank" rel="noreferrer">64.2-308.9</a>, the value of the augmented estate, to the extent provided in §§ <a href="http://law.lis.virginia.gov/vacode/64.2-308.5/" target="_blank" rel="noreferrer">64.2-308.5</a>, <a href="http://law.lis.virginia.gov/vacode/64.2-308.6/" target="_blank" rel="noreferrer">64.2-308.6</a>, <a href="http://law.lis.virginia.gov/vacode/64.2-308.7/" target="_blank" rel="noreferrer">64.2-308.7</a>, and <a href="http://law.lis.virginia.gov/vacode/64.2-308.8/" target="_blank" rel="noreferrer">64.2-308.8</a>, consists of the sum of the values of all property, whether real or personal, movable or immovable, tangible or intangible, wherever situated, that constitute:</p><ol><li><p>The decedent&#39;s net probate estate;</p></li><li><p>The decedent&#39;s non-probate transfers to others;</p></li><li><p>The decedent&#39;s non-probate transfers to the surviving spouse; and</p></li><li><p>The surviving spouse&#39;s property and non-probate transfers to others.</p></li></ol><p>B. The value of the marital-property portion of the augmented estate consists of the sum of the values of the four components of the augmented estate as determined under subsection A multiplied by the following percentage:</p><p>If the decedent and the spouse were married to each other: The percentage is:</p><table><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td>a</td><td>Less than 1 year</td><td>3%</td></tr><tr><td>a</td><td>1 year but less than 2 years</td><td>6%</td></tr><tr><td>b</td><td>2 years but less than 3 years</td><td>12%</td></tr><tr><td>c</td><td>3 years but less than 4 years</td><td>18%</td></tr><tr><td>d</td><td>4 years but less than 5 years</td><td>24%</td></tr><tr><td>e</td><td>5 years but less than 6 years</td><td>30%</td></tr><tr><td>f</td><td>6 years but less than 7 years</td><td>36%</td></tr><tr><td>g</td><td>7 years but less than 8 years</td><td>42%</td></tr><tr><td>h</td><td>8 years but less than 9 years</td><td>48%</td></tr><tr><td>i</td><td>9 years but less than 10 years</td><td>54%</td></tr><tr><td>j</td><td>10 years but less than 11 years</td><td>60%</td></tr><tr><td>k</td><td>11 years but less than 12 years</td><td>68%</td></tr><tr><td>l</td><td>12 years but less than 13 years</td><td>76%</td></tr><tr><td>m</td><td>13 years but less than 14 years</td><td>84%</td></tr><tr><td>n</td><td>14 years but less than 15 years</td><td>92%</td></tr><tr><td>o</td><td>15 years or more</td><td>100%</td></tr></tbody></table><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0187" target="_blank" rel="noreferrer">187</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>.</p>',7),s=[o];function n(i,l,h,p,c,u){return e(),r("div",null,s)}const b=t(d,[["render",n]]);export{g as __pageData,b as default};
