import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse(`{"title":"§ 64.2-308.8 Surviving spouse's property and non-probate transfers to others.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-308.8.md","filePath":"vacode/64.2-308.8.md"}`),s={name:"vacode/64.2-308.8.md"},n=o('<h1 id="§-64-2-308-8-surviving-spouse-s-property-and-non-probate-transfers-to-others" tabindex="-1">§ 64.2-308.8 Surviving spouse&#39;s property and non-probate transfers to others. <a class="header-anchor" href="#§-64-2-308-8-surviving-spouse-s-property-and-non-probate-transfers-to-others" aria-label="Permalink to &quot;§ 64.2-308.8 Surviving spouse&#39;s property and non-probate transfers to others.&quot;">​</a></h1><p>A. Except to the extent included in the augmented estate under § <a href="http://law.lis.virginia.gov/vacode/64.2-308.5/" target="_blank" rel="noreferrer">64.2-308.5</a> or <a href="http://law.lis.virginia.gov/vacode/64.2-308.7/" target="_blank" rel="noreferrer">64.2-308.7</a>, the value of the augmented estate includes the value of:</p><ol><li>Property that was owned by the decedent&#39;s surviving spouse at the decedent&#39;s death, including:</li></ol><p>a. The surviving spouse&#39;s fractional interest in property held in joint tenancy with the right of survivorship;</p><p>b. The surviving spouse&#39;s ownership interest in property or accounts held in co-ownership registration with the right of survivorship; and</p><p>c. Property that passed to the surviving spouse by reason of the decedent&#39;s death, but not including the spouse&#39;s right to homestead allowance, family allowance, exempt property, or payments under the federal social security system.</p><ol start="2"><li>Property that would have been included in the surviving spouse&#39;s non-probate transfers to others, other than the spouse&#39;s fractional and ownership interests included under subdivision 1 a or b, had the spouse been the decedent.</li></ol><p>B. Property included under this section is valued at the decedent&#39;s death, taking the fact that the decedent predeceased the spouse into account, but, for purposes of subdivision A 1 a or b, the values of the spouse&#39;s fractional and ownership interests are determined immediately before the decedent&#39;s death if the decedent was then a joint tenant or a co-owner of the property or accounts. For purposes of subdivision A 2, proceeds of insurance that would have been included in the spouse&#39;s non-probate transfers to others under subdivision 1 d of § <a href="http://law.lis.virginia.gov/vacode/64.2-308.6/" target="_blank" rel="noreferrer">64.2-308.6</a> are not valued as if the spouse were deceased.</p><p>C. The value of property included under this section is reduced by enforceable claims against the surviving spouse.</p><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0187" target="_blank" rel="noreferrer">187</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>.</p>',10),a=[n];function i(d,p,h,u,c,l){return t(),r("div",null,a)}const g=e(s,[["render",i]]);export{f as __pageData,g as default};
