import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 64.2-314 Waiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-314.md","filePath":"vacode/64.2-314.md"}'),i={name:"vacode/64.2-314.md"},o=t('<h1 id="§-64-2-314-waiver" tabindex="-1">§ 64.2-314 Waiver. <a class="header-anchor" href="#§-64-2-314-waiver" aria-label="Permalink to &quot;§ 64.2-314 Waiver.&quot;">​</a></h1><p>A. The right of a decedent&#39;s surviving spouse to a homestead allowance in the estate of a decedent as provided in § <a href="http://law.lis.virginia.gov/vacode/64.2-311/" target="_blank" rel="noreferrer">64.2-311</a> may be waived during the decedent&#39;s lifetime only by execution of a marital or premarital agreement in accordance with Chapter 8 (§ <a href="http://law.lis.virginia.gov/vacode/20-147/" target="_blank" rel="noreferrer">20-147</a> et seq.) of Title 20 or by execution of a waiver provided (i) the waiver is in writing, (ii) the language of the waiver mentions homestead allowance in conspicuous language, and (iii) the waiver has been signed by the surviving spouse.</p><p>B. The right to the family allowance and exempt property, as provided in §§ <a href="http://law.lis.virginia.gov/vacode/64.2-309/" target="_blank" rel="noreferrer">64.2-309</a> and <a href="http://law.lis.virginia.gov/vacode/64.2-310/" target="_blank" rel="noreferrer">64.2-310</a>, may be waived during the decedent&#39;s lifetime only by execution of a marital or premarital agreement made in accordance with Chapter 8 (§ <a href="http://law.lis.virginia.gov/vacode/20-147/" target="_blank" rel="noreferrer">20-147</a> et seq.) of Title 20.</p><p>1990, c. 831, § 64.1-151.6; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),n=[o];function l(d,s,c,h,v,p){return a(),r("div",null,n)}const f=e(i,[["render",l]]);export{_ as __pageData,f as default};
