import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-435 Requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-435.md","filePath":"vacode/64.2-435.md"}'),s={name:"vacode/64.2-435.md"},a=i('<h1 id="§-64-2-435-requirements" tabindex="-1">§ 64.2-435 Requirements. <a class="header-anchor" href="#§-64-2-435-requirements" aria-label="Permalink to &quot;§ 64.2-435 Requirements.&quot;">​</a></h1><p>A. The will shall be made in writing. It need not be written by the testator himself. It may be written in any language, by hand or by any other means.</p><p>B. The testator shall declare in the presence of two witnesses and of a person authorized to act in connection with international wills that the document is his will and that he knows the contents thereof. The testator need not inform the witnesses, or the authorized person, of the contents of the will.</p><p>C. In the presence of the witnesses, and of the authorized person, the testator shall sign the will or, if he has previously signed it, shall acknowledge his signature.</p><p>D. When the testator is unable to sign, the absence of his signature does not affect the validity of the international will if the testator indicates the reason for his inability to sign and the authorized person makes note thereof on the will. In these cases, it is permissible for any other person present, including the authorized person or one of the witnesses, at the direction of the testator to sign the testator&#39;s name for him, if the authorized person makes note of this also on the will, but it is not required that any person sign the testator&#39;s name for him.</p><p>E. The witnesses and the authorized person shall there and then attest the will by signing in the presence of the testator.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>, § 64.1-96.4; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',7),o=[a];function r(h,l,d,c,p,f){return t(),n("div",null,o)}const m=e(s,[["render",r]]);export{u as __pageData,m as default};
