import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 64.2-455 Wills to be recorded; recording copies; effect; transfer to The Library of Virginia.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-455.md","filePath":"vacode/64.2-455.md"}'),o={name:"vacode/64.2-455.md"},a=t('<h1 id="§-64-2-455-wills-to-be-recorded-recording-copies-effect-transfer-to-the-library-of-virginia" tabindex="-1">§ 64.2-455 Wills to be recorded; recording copies; effect; transfer to The Library of Virginia. <a class="header-anchor" href="#§-64-2-455-wills-to-be-recorded-recording-copies-effect-transfer-to-the-library-of-virginia" aria-label="Permalink to &quot;§ 64.2-455 Wills to be recorded; recording copies; effect; transfer to The Library of Virginia.&quot;">​</a></h1><p>A. Every will or authenticated copy admitted to probate by any circuit court or clerk of any circuit court shall be recorded by the clerk and remain in the clerk&#39;s office, except during such time as the same may be carried to another court under a subpoena duces tecum or as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/17.1-213/" target="_blank" rel="noreferrer">17.1-213</a>. A certified copy of such will or of any authenticated copy may be recorded in any county or city wherein there is any estate, real or personal, devised or bequeathed by such will.</p><p>B. The personal representative of the testator shall cause a certified copy of any will or of any authenticated copy so admitted to record to be recorded in any county or city wherein there is any real estate of which the testator possessed at the time of his death or that is devised by his will.</p><p>C. Every will or certified copy when recorded shall have the effect of notice to all persons of any devise or disposal by the will of real estate situated in a county or city in which such will or copy is so recorded.</p><p>D. With the approval of the judges of a circuit court of any county or city, the clerk of such court may transfer such original wills from his office to the Archives Division of The Library of Virginia. A copy of any will that has been microfilmed or stored in an electronic medium, prepared from such microfilmed or electronic record and certified as authentic by the clerk or his designee, shall constitute a certified copy of the will for any purpose arising under this title for which a certified copy of the will is required.</p><p>Code 1950, § 64-90; 1964, c. 169; 1966, c. 254; 1968, c. 656, § 64.1-94; 1978, c. 366; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0836" target="_blank" rel="noreferrer">836</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',6),c=[a];function n(s,l,d,h,f,p){return r(),i("div",null,c)}const g=e(o,[["render",n]]);export{y as __pageData,g as default};
