import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-520.1 Action for damages from legal malpractice concerning estate planning.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-520.1.md","filePath":"vacode/64.2-520.1.md"}'),i={name:"vacode/64.2-520.1.md"},o=n('<h1 id="§-64-2-520-1-action-for-damages-from-legal-malpractice-concerning-estate-planning" tabindex="-1">§ 64.2-520.1 Action for damages from legal malpractice concerning estate planning. <a class="header-anchor" href="#§-64-2-520-1-action-for-damages-from-legal-malpractice-concerning-estate-planning" aria-label="Permalink to &quot;§ 64.2-520.1 Action for damages from legal malpractice concerning estate planning.&quot;">​</a></h1><p>A. An action for damages to an individual or an individual&#39;s estate, including future tax liability, resulting from legal malpractice concerning the individual&#39;s estate planning, including the provision of legal advice or the preparation of legal documents, regardless of when executed, shall accrue upon completion of the representation during which the malpractice occurred.</p><p>B. Notwithstanding § <a href="/vacode/55.1-119/">55.1-119</a>, but subject to any written agreement between the individual and the defendant that expressly grants standing to a person who is not a party to the representation by specific reference to this subsection, the action may be maintained only by the individual or by the individual&#39;s personal representative.</p><p>C. An action for damages pursuant to this section in which a written contract for legal services existed between the individual and the defendant shall be brought within five years after the cause of action accrues as provided in this section. An action for damages pursuant to this section in which an unwritten contract for legal services existed between the individual and the defendant shall be brought within three years after the cause of action accrues as provided in this section.</p><p>D. Notwithstanding the provisions of this section, no such action shall be based upon damages that may reasonably be avoided or that result from a change of law subsequent to the representation upon which the action is based.</p><p>E. Any action pursuant to this section shall survive pursuant to § <a href="/vacode/8.01-25/">8.01-25</a>.</p><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0093" target="_blank" rel="noreferrer">93</a>.</p>',7),r=[o];function s(c,l,d,h,p,g){return a(),t("div",null,r)}const m=e(i,[["render",s]]);export{f as __pageData,m as default};
