import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 64.2-528 Order in which debts and demands of decedents to be paid.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-528.md","filePath":"vacode/64.2-528.md"}'),i={name:"vacode/64.2-528.md"},n=r('<h1 id="§-64-2-528-order-in-which-debts-and-demands-of-decedents-to-be-paid" tabindex="-1">§ 64.2-528 Order in which debts and demands of decedents to be paid. <a class="header-anchor" href="#§-64-2-528-order-in-which-debts-and-demands-of-decedents-to-be-paid" aria-label="Permalink to &quot;§ 64.2-528 Order in which debts and demands of decedents to be paid.&quot;">​</a></h1><p>When the assets of the decedent in his personal representative&#39;s possession are not sufficient to satisfy all debts and demands against him, they shall be applied to the payment of such debts and demands in the following order:</p><ol><li><p>Costs and expenses of administration;</p></li><li><p>The allowances provided in Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/64.2-309/" target="_blank" rel="noreferrer">64.2-309</a> et seq.) of Chapter 3;</p></li><li><p>Funeral expenses not to exceed $4,000;</p></li><li><p>Debts and taxes with preference under federal law;</p></li><li><p>Medical and hospital expenses of the last illness of the decedent, including compensation of persons attending him not to exceed $2,150 for each hospital and nursing home and $425 for each person furnishing services or goods;</p></li><li><p>Debts and taxes due the Commonwealth;</p></li><li><p>Debts due as trustee for persons under disabilities; as receiver or commissioner under decree of court of the Commonwealth; as personal representative, guardian, conservator, or committee when the qualification was in the Commonwealth; and for moneys collected by anyone to the credit of another and not paid over, regardless of whether or not a bond has been executed for the faithful performance of the duties of the party so collecting such funds;</p></li><li><p>Debts for child support arrearages;</p></li><li><p>Debts and taxes due localities and municipal corporations of the Commonwealth; and</p></li><li><p>All other claims.</p></li></ol><p>No preference shall be given in the payment of any claim over any other claim of the same class, and a claim due and payable shall not be entitled to a preference over a claim not due.</p><p>Code 1950, § 64-147; 1956, c. 231; 1966, c. 274; 1968, c. 656, § 64.1-157; 1972, c. 96; 1981, c. 580; 1986, c. 109; 1993, c. 259; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0666" target="_blank" rel="noreferrer">666</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0591" target="_blank" rel="noreferrer">591</a>.</p>',5),o=[n];function s(l,d,c,p,h,f){return a(),t("div",null,o)}const b=e(i,[["render",s]]);export{m as __pageData,b as default};
