import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 64.2-543 Contrary provisions of will or other instrument to govern.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-543.md","filePath":"vacode/64.2-543.md"}'),n={name:"vacode/64.2-543.md"},a=o('<h1 id="§-64-2-543-contrary-provisions-of-will-or-other-instrument-to-govern" tabindex="-1">§ 64.2-543 Contrary provisions of will or other instrument to govern. <a class="header-anchor" href="#§-64-2-543-contrary-provisions-of-will-or-other-instrument-to-govern" aria-label="Permalink to &quot;§ 64.2-543 Contrary provisions of will or other instrument to govern.&quot;">​</a></h1><p>A. For purposes of this section:</p><p>&quot;Includable beneficial interest&quot; means any property, interest, or benefit included in a person&#39;s estate for estate tax purposes that passes pursuant to an instrument other than such person&#39;s will.</p><p>B. The provisions of this article shall not impair the right or power of any person by will or by written instrument executed inter vivos to make direction for the payment of estate taxes and to designate the fund or property out of which such payment shall be made. Such designated funds or property may, in addition to any property passing by testate or intestate succession, include any includable beneficial interest. Unless a larger amount is charged to a specific includable beneficial interest by the instrument creating the interest, the maximum amount of tax that each such includable beneficial interest may be charged shall be limited to its share, as determined pursuant to § <a href="http://law.lis.virginia.gov/vacode/64.2-540/" target="_blank" rel="noreferrer">64.2-540</a> for the apportionment of taxes.</p><p>Code 1950, § 64-155; 1968, c. 656, § 64.1-165; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0917" target="_blank" rel="noreferrer">917</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',5),i=[a];function s(l,c,p,h,d,u){return t(),r("div",null,i)}const _=e(n,[["render",s]]);export{m as __pageData,_ as default};
