import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 64.2-604 Payment or delivery of small asset; funeral expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-604.md","filePath":"vacode/64.2-604.md"}'),n={name:"vacode/64.2-604.md"},o=r('<h1 id="§-64-2-604-payment-or-delivery-of-small-asset-funeral-expenses" tabindex="-1">§ 64.2-604 Payment or delivery of small asset; funeral expenses. <a class="header-anchor" href="#§-64-2-604-payment-or-delivery-of-small-asset-funeral-expenses" aria-label="Permalink to &quot;§ 64.2-604 Payment or delivery of small asset; funeral expenses.&quot;">​</a></h1><p>Thirty days after the death of a decedent upon whose estate there shall have been no application for the appointment of a personal representative pending or granted in any jurisdiction, any person holding a small asset belonging to the decedent may, at the request of a successor, pay or deliver so much of the small asset as does not exceed the amount given priority by § <a href="http://law.lis.virginia.gov/vacode/64.2-528/" target="_blank" rel="noreferrer">64.2-528</a> to the undertaker or mortuary handling the funeral of the decedent, and a receipt of the payee shall be a full and final release of the payor as to such sum.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>, § 64.1-132.5; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),s=[o];function l(i,d,p,c,f,h){return a(),t("div",null,s)}const u=e(n,[["render",l]]);export{m as __pageData,u as default};
