import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 64.2-606 Transfer of certain vessels registered with U.S. Coast Guard and transfer of motor vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-606.md","filePath":"vacode/64.2-606.md"}'),s={name:"vacode/64.2-606.md"},n=r('<h1 id="§-64-2-606-transfer-of-certain-vessels-registered-with-u-s-coast-guard-and-transfer-of-motor-vehicles" tabindex="-1">§ 64.2-606 Transfer of certain vessels registered with U.S. Coast Guard and transfer of motor vehicles. <a class="header-anchor" href="#§-64-2-606-transfer-of-certain-vessels-registered-with-u-s-coast-guard-and-transfer-of-motor-vehicles" aria-label="Permalink to &quot;§ 64.2-606 Transfer of certain vessels registered with U.S. Coast Guard and transfer of motor vehicles.&quot;">​</a></h1><p>A. When a resident of the Commonwealth owning a vessel registered with the U.S. Coast Guard dies and there has been no qualification on the decedent&#39;s estate, a transfer of ownership may be made by a legatee or distributee if he presents a statement made by him to the U.S. Coast Guard stating that (i) there has not been and there is not expected to be a qualification on the estate and (ii) the decedent&#39;s debts have been paid in full or that the proceeds from the sale of such vessel will be used to apply against the decedent&#39;s debts. The statement shall state the decedent&#39;s name, residence at the time of death, and date of death, and the names of all other persons, if any, having an interest in the vessel who, if they have reached the age of majority, shall signify in writing their consent to such transfer of title.</p><p>B. A transfer of ownership of a motor vehicle may be made by a legatee or distributee pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-634/" target="_blank" rel="noreferrer">46.2-634</a>.</p><p>1980, c. 731, § 64.1-123.2; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0399" target="_blank" rel="noreferrer">399</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),o=[n];function i(h,d,l,f,c,p){return t(),a("div",null,o)}const m=e(s,[["render",i]]);export{g as __pageData,m as default};
