import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 64.2-628 Requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-628.md","filePath":"vacode/64.2-628.md"}'),o={name:"vacode/64.2-628.md"},i=a('<h1 id="§-64-2-628-requirements" tabindex="-1">§ 64.2-628 Requirements. <a class="header-anchor" href="#§-64-2-628-requirements" aria-label="Permalink to &quot;§ 64.2-628 Requirements.&quot;">​</a></h1><p>A transfer on death deed:</p><ol><li><p>Except as otherwise provided in subdivision 2, shall contain the essential elements and formalities of a properly recordable inter vivos deed or document to convey a cooperative interest created pursuant to the Virginia Real Estate Cooperative Act (§ <a href="/vacode/55.1-2100/">55.1-2100</a> et seq.);</p></li><li><p>Shall state that the transfer to the designated beneficiary is to occur at the transferor&#39;s death;</p></li><li><p>Shall be recorded before the transferor&#39;s death in the land records of the clerk&#39;s office of the circuit court in the jurisdiction where the property is located;</p></li><li><p>Shall comply with the requirements for recordation set forth in Chapter 6 (§ <a href="/vacode/55.1-600/">55.1-600</a> et seq.) of Title 55.1 and shall be indexed by the clerk of court under the name of the transferor as grantor;</p></li><li><p>Unless the transfer is for consideration, shall be exempt from recordation tax as provided by subsection J of § <a href="/vacode/58.1-811/">58.1-811</a>;</p></li><li><p>For property owned by joint owners to be effective, shall be executed by all joint owners; and</p></li><li><p>Shall be considered a deed for purposes of complying with the requirements of § <a href="/vacode/17.1-223/">17.1-223</a>.</p></li></ol><p>2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0309" target="_blank" rel="noreferrer">309</a>.</p>',4),n=[i];function s(l,c,d,h,p,f){return t(),r("div",null,n)}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
