import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-727 Noncharitable trust without ascertainable beneficiary.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-727.md","filePath":"vacode/64.2-727.md"}'),i={name:"vacode/64.2-727.md"},o=a('<h1 id="§-64-2-727-noncharitable-trust-without-ascertainable-beneficiary" tabindex="-1">§ 64.2-727 Noncharitable trust without ascertainable beneficiary. <a class="header-anchor" href="#§-64-2-727-noncharitable-trust-without-ascertainable-beneficiary" aria-label="Permalink to &quot;§ 64.2-727 Noncharitable trust without ascertainable beneficiary.&quot;">​</a></h1><p>Except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/64.2-726/" target="_blank" rel="noreferrer">64.2-726</a> or by another statute, the following rules apply:</p><ol><li><p>A trust may be created for a noncharitable purpose without a definite or definitely ascertainable beneficiary or for a noncharitable but otherwise valid purpose to be selected by the trustee. The trust may not be enforced for more than 21 years.</p></li><li><p>A trust authorized by this section may be enforced by a person appointed in the terms of the trust or, if no person is so appointed, by a person appointed by the court.</p></li><li><p>Property of a trust authorized by this section may be applied only to its intended use, except to the extent the court determines that the value of the trust property exceeds the amount required for the intended use. Except as otherwise provided in the terms of the trust, property not required for the intended use shall be distributed to the settlor, if then living, otherwise to the settlor&#39;s successors in interest.</p></li></ol><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0935" target="_blank" rel="noreferrer">935</a>, § 55-544.09; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),n=[o];function s(l,c,h,p,d,u){return t(),r("div",null,n)}const _=e(i,[["render",s]]);export{f as __pageData,_ as default};
