import{_ as r,o as e,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 64.2-728 Modification or termination of trust; proceedings for approval or disapproval.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-728.md","filePath":"vacode/64.2-728.md"}'),a={name:"vacode/64.2-728.md"},i=t('<h1 id="§-64-2-728-modification-or-termination-of-trust-proceedings-for-approval-or-disapproval" tabindex="-1">§ 64.2-728 Modification or termination of trust; proceedings for approval or disapproval. <a class="header-anchor" href="#§-64-2-728-modification-or-termination-of-trust-proceedings-for-approval-or-disapproval" aria-label="Permalink to &quot;§ 64.2-728 Modification or termination of trust; proceedings for approval or disapproval.&quot;">​</a></h1><p>A. In addition to the methods of termination prescribed by §§ <a href="http://law.lis.virginia.gov/vacode/64.2-729/" target="_blank" rel="noreferrer">64.2-729</a> through <a href="http://law.lis.virginia.gov/vacode/64.2-732/" target="_blank" rel="noreferrer">64.2-732</a>, a trust terminates to the extent the trust is revoked or expires pursuant to its terms, no purpose of the trust remains to be achieved, or the purposes of the trust have become unlawful, contrary to public policy, or impossible to achieve.</p><p>B. A proceeding to approve or disapprove a proposed modification or termination under §§ <a href="http://law.lis.virginia.gov/vacode/64.2-729/" target="_blank" rel="noreferrer">64.2-729</a> through <a href="http://law.lis.virginia.gov/vacode/64.2-734/" target="_blank" rel="noreferrer">64.2-734</a>, or trust combination or division under § <a href="http://law.lis.virginia.gov/vacode/64.2-735/" target="_blank" rel="noreferrer">64.2-735</a>, may be commenced by a trustee or beneficiary. The settlor of a charitable trust may maintain a proceeding to modify the trust under § <a href="http://law.lis.virginia.gov/vacode/64.2-731/" target="_blank" rel="noreferrer">64.2-731</a>.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0935" target="_blank" rel="noreferrer">935</a>, § 55-544.10; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),n=[i];function s(p,l,c,d,f,h){return e(),o("div",null,n)}const _=r(a,[["render",s]]);export{g as __pageData,_ as default};
