import{_ as t,o as e,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-763 Duty to administer trust and invest.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-763.md","filePath":"vacode/64.2-763.md"}'),n={name:"vacode/64.2-763.md"},i=r('<h1 id="§-64-2-763-duty-to-administer-trust-and-invest" tabindex="-1">§ 64.2-763 Duty to administer trust and invest. <a class="header-anchor" href="#§-64-2-763-duty-to-administer-trust-and-invest" aria-label="Permalink to &quot;§ 64.2-763 Duty to administer trust and invest.&quot;">​</a></h1><p>Upon acceptance of a trusteeship, the trustee shall administer the trust and invest trust assets in good faith, in accordance with its terms and purposes and the interests of the beneficiaries, and in accordance with this chapter. In administering, managing and investing trust assets, the trustee shall comply with the provisions of the Uniform Prudent Investor Act (§ <a href="http://law.lis.virginia.gov/vacode/64.2-780/" target="_blank" rel="noreferrer">64.2-780</a> et seq.) and the Uniform Principal and Income Act (§ <a href="http://law.lis.virginia.gov/vacode/64.2-1000/" target="_blank" rel="noreferrer">64.2-1000</a> et seq.).</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0935" target="_blank" rel="noreferrer">935</a>, § 55-548.01; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',3),s=[i];function o(c,d,l,h,_,p){return e(),a("div",null,s)}const v=t(n,[["render",o]]);export{f as __pageData,v as default};
