import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-777 General powers of trustee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-777.md","filePath":"vacode/64.2-777.md"}'),a={name:"vacode/64.2-777.md"},s=o('<h1 id="§-64-2-777-general-powers-of-trustee" tabindex="-1">§ 64.2-777 General powers of trustee. <a class="header-anchor" href="#§-64-2-777-general-powers-of-trustee" aria-label="Permalink to &quot;§ 64.2-777 General powers of trustee.&quot;">​</a></h1><p>A. A trustee, without authorization by the court, may exercise:</p><ol><li><p>Powers conferred by the terms of the trust; and</p></li><li><p>Except as limited by the terms of the trust:</p></li></ol><p>a. All powers over the trust property that an unmarried competent owner has over individually owned property;</p><p>b. Any other powers appropriate to achieve the proper investment, management, and distribution of the trust property; and</p><p>c. Any other powers conferred by this chapter.</p><p>B. The exercise of a power is subject to the fiduciary duties prescribed by this article.</p><p>C. Any reference in a trust instrument incorporating the powers authorized under § <a href="http://law.lis.virginia.gov/vacode/64.2-105/" target="_blank" rel="noreferrer">64.2-105</a> shall not be construed to limit powers a trustee may exercise pursuant to this section, unless the settlor expressly states in the trust instrument that such reference should be so construed.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0935" target="_blank" rel="noreferrer">935</a>, § 55-548.15; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',9),i=[s];function n(p,c,l,h,u,d){return t(),r("div",null,i)}const m=e(a,[["render",n]]);export{f as __pageData,m as default};
