import{_ as t,o as e,c as r,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-779.1 Scope.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-779.1.md","filePath":"vacode/64.2-779.1.md"}'),a={name:"vacode/64.2-779.1.md"},i=o('<h1 id="§-64-2-779-1-scope" tabindex="-1">§ 64.2-779.1 Scope. <a class="header-anchor" href="#§-64-2-779-1-scope" aria-label="Permalink to &quot;§ 64.2-779.1 Scope.&quot;">​</a></h1><p>A. Except as otherwise provided in subsections B and C, this article applies to an express trust that is irrevocable or revocable by the settlor only with the consent of the trustee or a person holding an adverse interest.</p><p>B. This article does not apply to a trust held solely for charitable purposes.</p><p>C. Subject to § <a href="http://law.lis.virginia.gov/vacode/64.2-779.12/" target="_blank" rel="noreferrer">64.2-779.12</a>, a trust instrument may restrict or prohibit exercise of the decanting power.</p><p>D. This article does not limit the power of a trustee, powerholder, or other person to distribute or appoint property in further trust or to modify a trust under the trust instrument, a law of the Commonwealth other than this article, common law, a court order, or a nonjudicial settlement agreement.</p><p>E. This article does not affect the ability of a settlor to provide in a trust instrument for the distribution of the trust property or appointment in further trust of the trust property or for modification of the trust instrument.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0592" target="_blank" rel="noreferrer">592</a>.</p>',7),s=[i];function n(p,c,l,h,d,u){return e(),r("div",null,s)}const m=t(a,[["render",n]]);export{f as __pageData,m as default};
