import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 64.2-779.2 Fiduciary duty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/64.2-779.2.md","filePath":"vacode/64.2-779.2.md"}'),i={name:"vacode/64.2-779.2.md"},o=r('<h1 id="§-64-2-779-2-fiduciary-duty" tabindex="-1">§ 64.2-779.2 Fiduciary duty. <a class="header-anchor" href="#§-64-2-779-2-fiduciary-duty" aria-label="Permalink to &quot;§ 64.2-779.2 Fiduciary duty.&quot;">​</a></h1><p>A. In exercising the decanting power, an authorized fiduciary shall act in accordance with its fiduciary duties, including the duty to act in accordance with the purposes of the first trust.</p><p>B. This article does not create or imply a duty to exercise the decanting power or to inform beneficiaries about the applicability of this article.</p><p>C. Except as otherwise provided in a first-trust instrument, for purposes of this article and § <a href="http://law.lis.virginia.gov/vacode/64.2-763/" target="_blank" rel="noreferrer">64.2-763</a> and subsection A of § <a href="http://law.lis.virginia.gov/vacode/64.2-764/" target="_blank" rel="noreferrer">64.2-764</a>, the terms of the first trust are deemed to include the decanting power.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0592" target="_blank" rel="noreferrer">592</a>.</p>',5),c=[o];function n(s,d,l,p,h,u){return t(),a("div",null,c)}const g=e(i,[["render",n]]);export{f as __pageData,g as default};
