import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 65.2-102 Coverage of firefighters and law-enforcement officers in off-duty capacity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-102.md","filePath":"vacode/65.2-102.md"}'),o={name:"vacode/65.2-102.md"},t=a('<h1 id="§-65-2-102-coverage-of-firefighters-and-law-enforcement-officers-in-off-duty-capacity" tabindex="-1">§ 65.2-102 Coverage of firefighters and law-enforcement officers in off-duty capacity. <a class="header-anchor" href="#§-65-2-102-coverage-of-firefighters-and-law-enforcement-officers-in-off-duty-capacity" aria-label="Permalink to &quot;§ 65.2-102 Coverage of firefighters and law-enforcement officers in off-duty capacity.&quot;">​</a></h1><p>A. Notwithstanding any other provision of law, a claim for workers&#39; compensation benefits shall be deemed to be in the course of employment of any firefighter or law-enforcement officer who, in an off-duty capacity or outside an assigned shift or work location, undertakes any law-enforcement or rescue activity. Nothing in this section shall prohibit an employer from using any defense otherwise available under this title.</p><p>B. For purposes of this section:</p><p>&quot;Firefighter&quot; means all (i) salaried firefighters, including special forest wardens designated pursuant to § <a href="http://law.lis.virginia.gov/vacode/10.1-1135/" target="_blank" rel="noreferrer">10.1-1135</a>, emergency medical services personnel, and arson investigators and (ii) volunteer firefighters and emergency medical services personnel, if the governing body of the political subdivision in which the principal office of such volunteer fire company or volunteer emergency medical services agency is located has adopted a resolution acknowledging such volunteer fire company or volunteer emergency medical services agency as employees for purposes of this title.</p><p>&quot;Law-enforcement officer&quot; means all (i) members of county, city, town, or authority police departments, (ii) sheriffs and deputy sheriffs, (iii) auxiliary or reserve police and auxiliary or reserve deputy sheriffs, if the governing body of the political subdivision in which the principal office of such auxiliary or reserve police and auxiliary or reserve deputy sheriff force is located has adopted a resolution acknowledging such auxiliary or reserve police and auxiliary or reserve deputy sheriffs as employees for purposes of this title, (iv) members of the State Police Officers&#39; Retirement System, and (v) members of the Capitol Police as described in § <a href="http://law.lis.virginia.gov/vacode/30-34.2:1/" target="_blank" rel="noreferrer">30-34.2:1</a>.</p><p>1993, c. 719; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0330" target="_blank" rel="noreferrer">330</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0109" target="_blank" rel="noreferrer">109</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',6),n=[t];function s(f,c,l,d,p,h){return i(),r("div",null,n)}const v=e(o,[["render",s]]);export{u as __pageData,v as default};
