import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 65.2-1104 Award for compensation, medical treatment and vocational rehabilitation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-1104.md","filePath":"vacode/65.2-1104.md"}'),o={name:"vacode/65.2-1104.md"},r=i('<h1 id="§-65-2-1104-award-for-compensation-medical-treatment-and-vocational-rehabilitation" tabindex="-1">§ 65.2-1104 Award for compensation, medical treatment and vocational rehabilitation. <a class="header-anchor" href="#§-65-2-1104-award-for-compensation-medical-treatment-and-vocational-rehabilitation" aria-label="Permalink to &quot;§ 65.2-1104 Award for compensation, medical treatment and vocational rehabilitation.&quot;">​</a></h1><p>Upon a determination by the Commission that an employer or carrier has paid compensation, medical expenses or vocational rehabilitation services on behalf of an employee under circumstances as set forth under § <a href="http://law.lis.virginia.gov/vacode/65.2-1103/" target="_blank" rel="noreferrer">65.2-1103</a> and if notice of a claim against the Second Injury Fund was given prior to payment of the benefits, the Commission shall enter an award from the Second Injury Fund in favor of such employer or carrier for: (i) reimbursement on a pro rata basis of the compensation paid for further disability as set forth in clause (v) of § <a href="http://law.lis.virginia.gov/vacode/65.2-1103/" target="_blank" rel="noreferrer">65.2-1103</a>, such prorating to be computed according to the number of weeks each impairment is allowed under the schedule in § <a href="http://law.lis.virginia.gov/vacode/65.2-503/" target="_blank" rel="noreferrer">65.2-503</a>; (ii) reimbursement of reasonable medical expenses on the same basis as set forth in subdivision (i) of this section, provided the second injury is to the same previously impaired member but such reimbursement shall not exceed $7,500; and (iii) reimbursement of reasonable vocational rehabilitation training service on the same basis as set forth in subdivision (i) of this section but said reimbursement not to exceed $7,500.</p><p>1980, c. 599, § 65.1-142.1; 1991, c. 355.</p>',3),n=[r];function s(c,d,l,m,h,f){return a(),t("div",null,n)}const b=e(o,[["render",s]]);export{u as __pageData,b as default};
