import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 65.2-1201 Financing; tax.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-1201.md","filePath":"vacode/65.2-1201.md"}'),r={name:"vacode/65.2-1201.md"},o=n('<h1 id="§-65-2-1201-financing-tax" tabindex="-1">§ 65.2-1201 Financing; tax. <a class="header-anchor" href="#§-65-2-1201-financing-tax" aria-label="Permalink to &quot;§ 65.2-1201 Financing; tax.&quot;">​</a></h1><p>A. For the purpose of providing funds for compensation benefits awarded against any uninsured or self-insured employer under any provision of this chapter, a tax not to exceed one-half of one percent shall be assessed, collected and paid into the state treasury by the same persons and in the same manner as set forth in Chapter 10 (§ <a href="/vacode/65.2-1000/">65.2-1000</a> et seq.) of this title.</p><p>B. This tax shall be in addition to the tax for the Workers&#39; Compensation Commission Administrative Fund and the tax for the Second Injury Fund and shall be held by the Comptroller of the Commonwealth solely for the payment of awards against such fund.</p><p>C. At the end of any calendar year in which the Uninsured Employer&#39;s Fund has to its credit a sum in excess of the next year&#39;s budgeted expenditures, the tax shall be suspended for the ensuing calendar year.</p><p>1977, c. 345, § 65.1-147; 1983, c. 421; 1986, c. 177; 1990, c. 606; 1991, c. 355; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0099" target="_blank" rel="noreferrer">99</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0388" target="_blank" rel="noreferrer">388</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0219" target="_blank" rel="noreferrer">219</a>.</p>',5),i=[o];function s(d,c,h,l,p,f){return a(),t("div",null,i)}const g=e(r,[["render",s]]);export{u as __pageData,g as default};
