import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 65.2-301.2 Employee classification; disaster; personal protective equipment not considered.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-301.2.md","filePath":"vacode/65.2-301.2.md"}'),o={name:"vacode/65.2-301.2.md"},n=i('<h1 id="§-65-2-301-2-employee-classification-disaster-personal-protective-equipment-not-considered" tabindex="-1">§ 65.2-301.2 Employee classification; disaster; personal protective equipment not considered. <a class="header-anchor" href="#§-65-2-301-2-employee-classification-disaster-personal-protective-equipment-not-considered" aria-label="Permalink to &quot;§ 65.2-301.2 Employee classification; disaster; personal protective equipment not considered.&quot;">​</a></h1><p>A. For the purposes of this section, the terms &quot;communicable disease of public health threat,&quot; &quot;disaster,&quot; and &quot;state of emergency&quot; have the same meaning as provided in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>B. In any proceeding under the provisions of this title, a hiring party providing an individual with personal protective equipment in response to a disaster caused by a communicable disease of public health threat for which a state of emergency has been declared pursuant to § <a href="/vacode/44-146.17/">44-146.17</a> shall not be considered in any determination regarding whether such individual is an employee or independent contractor.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0448" target="_blank" rel="noreferrer">448</a>.</p>',4),s=[n];function r(c,d,p,l,h,m){return t(),a("div",null,s)}const f=e(o,[["render",r]]);export{_ as __pageData,f as default};
