import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 65.2-306 When compensation not allowed for injury or death; burden of proof.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-306.md","filePath":"vacode/65.2-306.md"}'),a={name:"vacode/65.2-306.md"},n=r('<h1 id="§-65-2-306-when-compensation-not-allowed-for-injury-or-death-burden-of-proof" tabindex="-1">§ 65.2-306 When compensation not allowed for injury or death; burden of proof. <a class="header-anchor" href="#§-65-2-306-when-compensation-not-allowed-for-injury-or-death-burden-of-proof" aria-label="Permalink to &quot;§ 65.2-306 When compensation not allowed for injury or death; burden of proof.&quot;">​</a></h1><p>A. No compensation shall be awarded to the employee or his dependents for an injury or death caused by:</p><ol><li><p>The employee&#39;s willful misconduct or intentional self-inflicted injury;</p></li><li><p>The employee&#39;s attempt to injure another;</p></li><li><p>The employee&#39;s intoxication;</p></li><li><p>The employee&#39;s willful failure or refusal to use a safety appliance or perform a duty required by statute;</p></li><li><p>The employee&#39;s willful breach of any reasonable rule or regulation adopted by the employer and brought, prior to the accident, to the knowledge of the employee; or</p></li><li><p>The employee&#39;s use of a nonprescribed controlled substance identified as such in Chapter 34 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3400/" target="_blank" rel="noreferrer">54.1-3400</a> et seq.) of Title 54.1.</p></li></ol><p>B. The person or entity asserting any of the defenses in this section shall have the burden of proof with respect thereto. However, if the employer raises as a defense the employee&#39;s intoxication or use of a nonprescribed controlled substance identified as such in Chapter 34 of Title 54.1, and there was at the time of the injury an amount of alcohol or nonprescribed controlled substance in the bodily fluids of the employee which (i) is equal to or greater than the standard set forth in § <a href="http://law.lis.virginia.gov/vacode/18.2-266/" target="_blank" rel="noreferrer">18.2-266</a>, or (ii) in the case of use of a nonprescribed controlled substance, yields a positive test result from a Substance Abuse and Mental Health Services Administration (SAMHSA) certified laboratory, there shall be a rebuttable presumption, which presumption shall not be available if the employee dies as a result of his injuries, that the employee was intoxicated due to the consumption of alcohol or using a nonprescribed controlled substance at the time of his injury. The employee may overcome such a presumption by clear and convincing evidence.</p><p>Code 1950, § 65-35; 1968, c. 660, § 65.1-38; 1991, cc. 166, 355; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0600" target="_blank" rel="noreferrer">600</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0636" target="_blank" rel="noreferrer">636</a>.</p>',5),i=[n];function l(s,h,p,c,d,f){return o(),t("div",null,i)}const b=e(a,[["render",l]]);export{m as __pageData,b as default};
