import{_ as o,o as t,c as a,z as e,a as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 65.2-308 Discharge of employee for exercising rights prohibited; civil action; relief.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-308.md","filePath":"vacode/65.2-308.md"}'),r={name:"vacode/65.2-308.md"},s=e("h1",{id:"§-65-2-308-discharge-of-employee-for-exercising-rights-prohibited-civil-action-relief",tabindex:"-1"},[i("§ 65.2-308 Discharge of employee for exercising rights prohibited; civil action; relief. "),e("a",{class:"header-anchor",href:"#§-65-2-308-discharge-of-employee-for-exercising-rights-prohibited-civil-action-relief","aria-label":'Permalink to "§ 65.2-308 Discharge of employee for exercising rights prohibited; civil action; relief."'},"​")],-1),n=e("p",null,"A. No employer or person shall discharge an employee solely because the employee intends to file or has filed a claim under this title or has testified or is about to testify in any proceeding under this title. The discharge of a person who has filed a fraudulent claim is not a violation of this section.",-1),c=e("p",null,[i("B. The employee may bring an action in a circuit court having jurisdiction over the employer or person who allegedly discharged the employee in violation of this section. The court shall have jurisdiction, for cause shown, to restrain violations and order appropriate relief, including actual damages and attorney's fees to successful claimants and the rehiring or reinstatement of the employee, with back pay plus interest at the judgment rate as provided in § "),e("a",{href:"http://law.lis.virginia.gov/vacode/6.2-302/",target:"_blank",rel:"noreferrer"},"6.2-302"),i(".")],-1),l=e("p",null,"1982, c. 327, § 65.1-40.1; 1986, c. 259; 1991, c. 355.",-1),h=[s,n,c,l];function d(p,f,g,m,u,_){return t(),a("div",null,h)}const b=o(r,[["render",d]]);export{y as __pageData,b as default};
