import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 65.2-309.1 Creation of lien and subrogation of employer to employee's rights to recover uninsured or underinsured motorist benefits pursuant to insurance coverage carried by and at the expense of employer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-309.1.md","filePath":"vacode/65.2-309.1.md"}`),a={name:"vacode/65.2-309.1.md"},n=t('<h1 id="§-65-2-309-1-creation-of-lien-and-subrogation-of-employer-to-employee-s-rights-to-recover-uninsured-or-underinsured-motorist-benefits-pursuant-to-insurance-coverage-carried-by-and-at-the-expense-of-employer" tabindex="-1">§ 65.2-309.1 Creation of lien and subrogation of employer to employee&#39;s rights to recover uninsured or underinsured motorist benefits pursuant to insurance coverage carried by and at the expense of employer. <a class="header-anchor" href="#§-65-2-309-1-creation-of-lien-and-subrogation-of-employer-to-employee-s-rights-to-recover-uninsured-or-underinsured-motorist-benefits-pursuant-to-insurance-coverage-carried-by-and-at-the-expense-of-employer" aria-label="Permalink to &quot;§ 65.2-309.1 Creation of lien and subrogation of employer to employee&#39;s rights to recover uninsured or underinsured motorist benefits pursuant to insurance coverage carried by and at the expense of employer.&quot;">​</a></h1><p>A. A claim against an employer under this title for injury or death benefits shall create a lien and right of subrogation on behalf of the employer, as set forth in § <a href="http://law.lis.virginia.gov/vacode/65.2-309/" target="_blank" rel="noreferrer">65.2-309</a>, against proceeds recovered by the injured employee pursuant to the uninsured or underinsured motorist provisions of a policy of motor vehicle insurance carried by and at the expense of the employer. In any action by an employee against any person other than the employer, the court shall, after reasonable notice to the parties and the employer, ascertain the amount of compensation paid and expenses for medical, surgical and hospital attention and supplies, and funeral expenses incurred by the employer under the provisions of this title and deduct therefrom a proportionate share of such amounts as are paid by the plaintiff for reasonable expenses and attorney&#39;s fees as provided in § <a href="http://law.lis.virginia.gov/vacode/65.2-311/" target="_blank" rel="noreferrer">65.2-311</a>; and, in the event of judgment against such person other than the employer, the court shall, in its order, require that the judgment debtor pay such compensation and expenses of the employer, less said share of expenses and attorney&#39;s fees, so ascertained by the court out of the amount of the judgment, so far as sufficient, and the balance, if any, to the judgment creditor.</p><p>B. If an injured employee is entitled to underinsured motorist coverage under more than one policy, the order of priority shall be as provided by subsection B of § <a href="http://law.lis.virginia.gov/vacode/38.2-2206/" target="_blank" rel="noreferrer">38.2-2206</a>.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0267" target="_blank" rel="noreferrer">267</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>.</p>',4),i=[n];function s(d,l,p,u,c,h){return r(),o("div",null,i)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
