import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 65.2-312 False statements, representations, etc., in connection with an award; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-312.md","filePath":"vacode/65.2-312.md"}'),i={name:"vacode/65.2-312.md"},o=n('<h1 id="§-65-2-312-false-statements-representations-etc-in-connection-with-an-award-penalties" tabindex="-1">§ 65.2-312 False statements, representations, etc., in connection with an award; penalties. <a class="header-anchor" href="#§-65-2-312-false-statements-representations-etc-in-connection-with-an-award-penalties" aria-label="Permalink to &quot;§ 65.2-312 False statements, representations, etc., in connection with an award; penalties.&quot;">​</a></h1><p>A. It shall be unlawful for any person to knowingly make, file or use any writing or document knowing the same to contain any materially false, fictitious or fraudulent statement or entry in connection with an award under this title. It shall also be unlawful for any person to aid or abet another in a violation of this section.</p><p>B. A violation of this section shall be punishable as a Class 6 felony.</p><p>C. Any person convicted of a violation of this section who is licensed to practice any of the healing arts as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-2900/" target="_blank" rel="noreferrer">54.1-2900</a> or to practice law pursuant to Chapter 39 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3900/" target="_blank" rel="noreferrer">54.1-3900</a> et seq.) of Title 54.1, and who committed the violation while engaged in such practice, may have such license suspended or revoked in accordance with the provisions of Chapter 29 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-2900/" target="_blank" rel="noreferrer">54.1-2900</a> et seq.) and Chapter 39 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3900/" target="_blank" rel="noreferrer">54.1-3900</a> et seq.) of Title 54.1, respectively.</p><p>D. Venue for the prosecution of a violation of this section shall lie in the county or city wherein the injury occurred.</p><p>1993, c. 792; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0011" target="_blank" rel="noreferrer">11</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0366" target="_blank" rel="noreferrer">366</a>.</p>',6),r=[o];function s(l,c,h,p,d,f){return t(),a("div",null,r)}const u=e(i,[["render",s]]);export{v as __pageData,u as default};
