import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 65.2-601.1 Effect of filing claim; stay of debt collection activities by health care providers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-601.1.md","filePath":"vacode/65.2-601.1.md"}'),o={name:"vacode/65.2-601.1.md"},r=i('<h1 id="§-65-2-601-1-effect-of-filing-claim-stay-of-debt-collection-activities-by-health-care-providers" tabindex="-1">§ 65.2-601.1 Effect of filing claim; stay of debt collection activities by health care providers. <a class="header-anchor" href="#§-65-2-601-1-effect-of-filing-claim-stay-of-debt-collection-activities-by-health-care-providers" aria-label="Permalink to &quot;§ 65.2-601.1 Effect of filing claim; stay of debt collection activities by health care providers.&quot;">​</a></h1><p>A. Whenever an employee makes a claim pursuant to § <a href="http://law.lis.virginia.gov/vacode/65.2-601/" target="_blank" rel="noreferrer">65.2-601</a>, all health care providers, as defined in § <a href="http://law.lis.virginia.gov/vacode/8.01-581.1/" target="_blank" rel="noreferrer">8.01-581.1</a>, shall refrain from all debt collection activities relating to medical treatment received by the employee in connection with such claim until an award is made on the employee&#39;s claim pursuant to § <a href="http://law.lis.virginia.gov/vacode/65.2-704/" target="_blank" rel="noreferrer">65.2-704</a>. The statute of limitations for collection of such debt shall be tolled during the period in which the applicable health care provider is required to refrain from debt collection activities hereunder.</p><p>B. For the purpose of this section, &quot;debt collection activities&quot; means repeatedly calling or writing to the employee and threatening either to turn the matter over to a debt collection agency or to an attorney for collection, enforcement or filing of other process. The term shall not include routine billing or inquiries about the status of the claim.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0462" target="_blank" rel="noreferrer">462</a>.</p>',4),l=[r];function c(n,s,h,f,d,p){return t(),a("div",null,l)}const _=e(o,[["render",c]]);export{v as __pageData,_ as default};
