import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const y=JSON.parse(`{"title":"§ 65.2-601.2 Notice to employee of employer's intent.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-601.2.md","filePath":"vacode/65.2-601.2.md"}`),a={name:"vacode/65.2-601.2.md"},i=r('<h1 id="§-65-2-601-2-notice-to-employee-of-employer-s-intent" tabindex="-1">§ 65.2-601.2 Notice to employee of employer&#39;s intent. <a class="header-anchor" href="#§-65-2-601-2-notice-to-employee-of-employer-s-intent" aria-label="Permalink to &quot;§ 65.2-601.2 Notice to employee of employer&#39;s intent.&quot;">​</a></h1><p>A. Whenever an employee makes a claim pursuant to § <a href="/vacode/65.2-601/">65.2-601</a>, the Commission shall order the employer to advise the employee, within 30 days following the date of such order, whether the employer (i) intends to accept the claim, (ii) intends to deny the claim, or (iii) is unable to determine whether it intends to accept or deny the claim because the employer lacks sufficient information from the employee or a third party to make such determination. If the employer responds that it intends to deny the claim, the response shall provide reasons therefor. If the employer responds that it is unable to determine whether it intends to accept or deny the claim because it lacks sufficient information from the employee or a third party to make such determination, the response shall identify the additional information that the employer needs from the employee or a third party in order to make such determination.</p><p>B. The employer&#39;s response to the order shall be considered a required report for the purposes of § <a href="/vacode/65.2-902/">65.2-902</a>.</p><p>C. The employer&#39;s response to the order shall not be considered part of the hearing record.</p><p>D. An employer may, if the employee consents, send any response required by this section to the employee by email.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1086" target="_blank" rel="noreferrer">1086</a>.</p>',6),n=[i];function s(h,l,p,d,m,c){return t(),o("div",null,n)}const _=e(a,[["render",s]]);export{y as __pageData,_ as default};
