import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 65.2-605.2 Biennial peer-reviewed studies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-605.2.md","filePath":"vacode/65.2-605.2.md"}'),i={name:"vacode/65.2-605.2.md"},s=r('<h1 id="§-65-2-605-2-biennial-peer-reviewed-studies" tabindex="-1">§ 65.2-605.2 Biennial peer-reviewed studies. <a class="header-anchor" href="#§-65-2-605-2-biennial-peer-reviewed-studies" aria-label="Permalink to &quot;§ 65.2-605.2 Biennial peer-reviewed studies.&quot;">​</a></h1><p>A. The Commission shall have a peer-reviewed study conducted every two years commencing in 2016 by a reputable independent, not-for-profit research organization to determine how Virginia&#39;s workers&#39; compensation system and workers&#39; compensation medical costs compare with (i) those of other states&#39; systems and (ii) previous workers&#39; compensation medical benchmarks studies conducted in Virginia. Such studies shall also review the status of access to medical services under Virginia&#39;s workers&#39; compensation system.</p><p>B. The Commission shall pay for the studies conducted pursuant to subsection A through revenues generated pursuant to the administrative tax assessed pursuant to Chapter 10 (§ <a href="http://law.lis.virginia.gov/vacode/65.2-1000/" target="_blank" rel="noreferrer">65.2-1000</a> et seq.) and deposited in the fund established pursuant to § <a href="http://law.lis.virginia.gov/vacode/65.2-1007/" target="_blank" rel="noreferrer">65.2-1007</a>.</p><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0279" target="_blank" rel="noreferrer">279</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0290" target="_blank" rel="noreferrer">290</a>.</p>',4),n=[s];function o(d,c,l,p,h,u){return t(),a("div",null,n)}const v=e(i,[["render",o]]);export{m as __pageData,v as default};
