import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 65.2-706 Conclusiveness of award; appeal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-706.md","filePath":"vacode/65.2-706.md"}'),t={name:"vacode/65.2-706.md"},i=r('<h1 id="§-65-2-706-conclusiveness-of-award-appeal" tabindex="-1">§ 65.2-706 Conclusiveness of award; appeal. <a class="header-anchor" href="#§-65-2-706-conclusiveness-of-award-appeal" aria-label="Permalink to &quot;§ 65.2-706 Conclusiveness of award; appeal.&quot;">​</a></h1><p>A. The award of the Commission, as provided in § <a href="http://law.lis.virginia.gov/vacode/65.2-704/" target="_blank" rel="noreferrer">65.2-704</a>, if not reviewed in due time, or an award of the Commission upon such review, as provided in § <a href="http://law.lis.virginia.gov/vacode/65.2-705/" target="_blank" rel="noreferrer">65.2-705</a>, shall be conclusive and binding as to all questions of fact. No appeal shall be taken from the decision of one Commissioner until a review of the case has been had before the full Commission, as provided in § <a href="http://law.lis.virginia.gov/vacode/65.2-705/" target="_blank" rel="noreferrer">65.2-705</a>, and an award entered by it. Appeals shall lie from such award to the Court of Appeals in the manner provided in the Rules of the Supreme Court.</p><p>B. The notice of appeal shall be filed with the clerk of the Commission within 30 days from the date of such award. A copy of the notice of appeal shall be filed in the office of the clerk of the Court of Appeals as provided in the Rules of Court.</p><p>C. Cases so appealed shall be placed upon the privileged docket of the Court of Appeals and be heard at the next ensuing term thereof. In case of an appeal from the decision of the Commission to the Court of Appeals, or from the decision of the Court of Appeals to the Supreme Court, the appeal shall operate as a suspension of the award and no employer shall be required to make payment of the award involved in the appeal until the questions at issue therein shall have been fully determined in accordance with the provisions of this title.</p><p>Code 1950, § 65-94; 1968, c. 660, § 65.1-98; 1971, Ex. Sess., c. 156; 1972, c. 696; 1977, c. 624; 1984, cc. 659, 703; 1991, c. 355; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0938" target="_blank" rel="noreferrer">938</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0671" target="_blank" rel="noreferrer">671</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0160" target="_blank" rel="noreferrer">160</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0564" target="_blank" rel="noreferrer">564</a>.</p>',5),n=[i];function l(s,p,h,f,c,d){return a(),o("div",null,n)}const g=e(t,[["render",l]]);export{v as __pageData,g as default};
