import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 65.2-709 Cost of living supplements for total incapacity and dependents of deceased.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-709.md","filePath":"vacode/65.2-709.md"}'),i={name:"vacode/65.2-709.md"},o=n('<h1 id="§-65-2-709-cost-of-living-supplements-for-total-incapacity-and-dependents-of-deceased" tabindex="-1">§ 65.2-709 Cost of living supplements for total incapacity and dependents of deceased. <a class="header-anchor" href="#§-65-2-709-cost-of-living-supplements-for-total-incapacity-and-dependents-of-deceased" aria-label="Permalink to &quot;§ 65.2-709 Cost of living supplements for total incapacity and dependents of deceased.&quot;">​</a></h1><p>A. Cost of living supplements shall be payable under this section if:</p><ol><li><p>The combined disability benefit entitlement of a claimant or his dependents under this title and the federal Old-Age, Survivors, and Disability Insurance program is less than 80 percent of the average monthly earnings of the claimant before disability or death; or</p></li><li><p>The claimant or his dependents are receiving disability payments under this title but not benefits under the federal Old-Age, Survivors, and Disability Insurance program.</p></li></ol><p>Such cost of living supplements shall be payable, in addition to the other benefits payable under this title, in accordance with the provisions of this section to those recipients of awards resulting from occupational disease, accident, or death occurring on or after July 1, 1975, under § <a href="/vacode/65.2-500/">65.2-500</a>, subsection C of § <a href="/vacode/65.2-503/">65.2-503</a>, subdivision A 4 of § <a href="/vacode/65.2-504/">65.2-504</a>, and §§ <a href="/vacode/65.2-512/">65.2-512</a> and <a href="/vacode/65.2-513/">65.2-513</a>. For purposes of determining the monthly amount of combined disability entitlement received by a claimant pursuant to subdivision A 1, the claimant may deduct any monthly amounts paid for Medicare.</p><p>B. The Commission may require the claimant to present evidence of filing for federal Old-Age, Survivors, and Disability Insurance benefits in order to establish eligibility under subdivision A 1 and also may require the claimant to furnish the employer with the decision on his claim for such federal benefits.</p><p>C. The amounts of supplementary payments provided for herein shall be determined by using a compounding method of computation annually. The percentage of change shall be determined by reference to the increase, if any, in the United States Average Consumer Price Index for all items, as published by the Bureau of Labor Statistics of the U.S. Department of Labor, from its monthly average, from one calendar year to another.</p><p>D. Amounts of supplementary payments shall be based on the percentage increase, if any, in the Average Consumer Price Index for all items adjusted annually. Any change in the cost of living supplement determined as of any determination date shall become effective as of October 1 next following such determination date and, as the case may be, shall be added to or subtracted from any cost of living supplements previously payable; however, compensation paid the claimant under this section shall at no time exceed the then current maximum weekly amount payable under § <a href="/vacode/65.2-500/">65.2-500</a>.</p><p>1975, c. 472, § 65.1-99.1; 1981, c. 265; 1986, c. 548; 1991, c. 355; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0296" target="_blank" rel="noreferrer">296</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0182" target="_blank" rel="noreferrer">182</a>.</p>',8),s=[o];function r(d,l,c,p,f,h){return a(),t("div",null,s)}const b=e(i,[["render",r]]);export{u as __pageData,b as default};
