import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 65.2-805 Civil penalty for violation of §§ 65.2-800, 65.2-803.1, and 65.2-804.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-805.md","filePath":"vacode/65.2-805.md"}'),o={name:"vacode/65.2-805.md"},n=i('<h1 id="§-65-2-805-civil-penalty-for-violation-of-§§-65-2-800-65-2-803-1-and-65-2-804" tabindex="-1">§ 65.2-805 Civil penalty for violation of §§ 65.2-800, 65.2-803.1, and 65.2-804. <a class="header-anchor" href="#§-65-2-805-civil-penalty-for-violation-of-§§-65-2-800-65-2-803-1-and-65-2-804" aria-label="Permalink to &quot;§ 65.2-805 Civil penalty for violation of §§ 65.2-800, 65.2-803.1, and 65.2-804.&quot;">​</a></h1><p>A. If such employer fails to comply with the provisions of § <a href="http://law.lis.virginia.gov/vacode/65.2-800/" target="_blank" rel="noreferrer">65.2-800</a> or <a href="http://law.lis.virginia.gov/vacode/65.2-804/" target="_blank" rel="noreferrer">65.2-804</a>, he shall be assessed a civil penalty of not more than $250 per day for each day of noncompliance, subject to a maximum penalty of $50,000. Such employer also shall be liable during continuance of such failure to any employee either for compensation under this title or at law in a suit instituted by the employee against such employer to recover damages for personal injury or death by accident, and in any such suit such employer shall not be permitted to defend upon any of the following grounds:</p><ol><li><p>That the employee was negligent;</p></li><li><p>That the injury was caused by the negligence of a fellow employee; or</p></li><li><p>That the employee had assumed the risk of the injury.</p></li></ol><p>B. Any person who fails to comply with the provisions of § <a href="http://law.lis.virginia.gov/vacode/65.2-803.1/" target="_blank" rel="noreferrer">65.2-803.1</a> shall be assessed a civil penalty of not less than $500 nor more than $5,000 for each instance of noncompliance, in addition to any other penalties applicable under this title.</p><p>C. The civil penalties herein provided may be assessed by the Commission in an open hearing with the right of review and appeal as in other cases. Upon a finding by the Commission of such failure to comply, and after 15 days&#39; written notice thereof sent by certified mail to the employer, if such failure continues, the Commission may order the employer to cease and desist all business transactions and operations until found by the Commission to be in compliance with the provisions of this chapter.</p><p>D. Any civil penalty assessed pursuant to this section shall be divided equally between and paid into the administrative fund established in Chapter 10 (§ <a href="http://law.lis.virginia.gov/vacode/65.2-1000/" target="_blank" rel="noreferrer">65.2-1000</a> et seq.) and the Uninsured Employer&#39;s Fund established in Chapter 12 (§ <a href="http://law.lis.virginia.gov/vacode/65.2-1200/" target="_blank" rel="noreferrer">65.2-1200</a> et seq.). The Commission may add the costs of collection of such civil penalty to the aggregate civil penalty owed, in which event such costs shall be paid into the administrative fund established in Chapter 10 (§ <a href="http://law.lis.virginia.gov/vacode/65.2-1000/" target="_blank" rel="noreferrer">65.2-1000</a> et seq.).</p><p>Code 1950, § 65-102; 1968, c. 660, § 65.1-106; 1970, c. 470; 1974, c. 314; 1980, c. 443; 1991, c. 355; 1993, c. 378; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0204" target="_blank" rel="noreferrer">204</a>.</p>',7),r=[n];function l(s,h,c,p,d,f){return a(),t("div",null,r)}const u=e(o,[["render",l]]);export{v as __pageData,u as default};
