import{_ as o,o as r,c as t,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 65.2-812 Subrogation of insurance carrier to employer's rights; compromise.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-812.md","filePath":"vacode/65.2-812.md"}`),i={name:"vacode/65.2-812.md"},n=e("h1",{id:"§-65-2-812-subrogation-of-insurance-carrier-to-employer-s-rights-compromise",tabindex:"-1"},[a("§ 65.2-812 Subrogation of insurance carrier to employer's rights; compromise. "),e("a",{class:"header-anchor",href:"#§-65-2-812-subrogation-of-insurance-carrier-to-employer-s-rights-compromise","aria-label":`Permalink to "§ 65.2-812 Subrogation of insurance carrier to employer's rights; compromise."`},"​")],-1),s=e("p",null,"When any employer is insured against liability for compensation with an insurance carrier, and such insurance carrier shall have paid any compensation for which the employer is liable or shall have assumed the liability of the employer therefor, it shall be subrogated to all the rights and duties of the employer and may enforce any such rights in its own name or in the name of the injured employee or his personal representative; however, nothing herein shall be construed as conferring upon the insurance carriers any other or further rights than those existing in the employer at the time of the injury to his employee, anything in the policy of insurance to the contrary notwithstanding. No compromise settlement shall be made by the insurance carrier in the exercise of such right of subrogation without the approval of the Workers' Compensation Commission and the injured employee or the personal representative or dependents of the deceased employee being first obtained.",-1),h=e("p",null,"Code 1950, § 65-108; 1968, c. 660, § 65.1-112; 1991, c. 355.",-1),c=[n,s,h];function l(m,p,d,u,f,y){return r(),t("div",null,c)}const b=o(i,[["render",l]]);export{_ as __pageData,b as default};
