import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 65.2-819 Penalty for violation of certain provisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/65.2-819.md","filePath":"vacode/65.2-819.md"}'),t={name:"vacode/65.2-819.md"},n=r('<h1 id="§-65-2-819-penalty-for-violation-of-certain-provisions" tabindex="-1">§ 65.2-819 Penalty for violation of certain provisions. <a class="header-anchor" href="#§-65-2-819-penalty-for-violation-of-certain-provisions" aria-label="Permalink to &quot;§ 65.2-819 Penalty for violation of certain provisions.&quot;">​</a></h1><p>Any person or persons who shall in this Commonwealth (i) act or assume to act as agent for any such insurance carrier whose authority to do business in this Commonwealth has been suspended, while such suspension remains in force, (ii) fail to comply with requirements or standards imposed under §§ <a href="http://law.lis.virginia.gov/vacode/65.2-817/" target="_blank" rel="noreferrer">65.2-817</a> and <a href="http://law.lis.virginia.gov/vacode/65.2-818/" target="_blank" rel="noreferrer">65.2-818</a> or of Chapter 10 (§ <a href="http://law.lis.virginia.gov/vacode/65.2-1000/" target="_blank" rel="noreferrer">65.2-1000</a> et seq.) of this title, or (iii) willfully make a false or fraudulent statement of the business or condition of any such insurance carrier, or a false or fraudulent return as therein provided, shall be deemed guilty of a misdemeanor and, upon conviction, shall be punished by a fine of not less than $100 nor more than $1,000 or by imprisonment for not less than ten nor more than ninety days, or both such fine and imprisonment, in the discretion of the court or jury trying the case.</p><p>Code 1950, § 65-114; 1968, c. 660, § 65.1-117.1; 1970, c. 470; 1991, c. 355.</p>',3),i=[n];function s(l,c,h,d,f,p){return o(),a("div",null,i)}const m=e(t,[["render",s]]);export{_ as __pageData,m as default};
