import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 66-10.2 Board to establish regulations regarding youth detained in juvenile correctional facilities pursuant to contracts with the federal government.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/66-10.2.md","filePath":"vacode/66-10.2.md"}'),r={name:"vacode/66-10.2.md"},n=i('<h1 id="§-66-10-2-board-to-establish-regulations-regarding-youth-detained-in-juvenile-correctional-facilities-pursuant-to-contracts-with-the-federal-government" tabindex="-1">§ 66-10.2 Board to establish regulations regarding youth detained in juvenile correctional facilities pursuant to contracts with the federal government. <a class="header-anchor" href="#§-66-10-2-board-to-establish-regulations-regarding-youth-detained-in-juvenile-correctional-facilities-pursuant-to-contracts-with-the-federal-government" aria-label="Permalink to &quot;§ 66-10.2 Board to establish regulations regarding youth detained in juvenile correctional facilities pursuant to contracts with the federal government.&quot;">​</a></h1><p>The Board, in collaboration with the Department of Behavioral Health and Developmental Services, shall promulgate regulations governing the housing of youth who are detained in a juvenile correctional facility pursuant to a contract with the federal government and not committed to such juvenile correctional facility by a court of the Commonwealth. Such regulations shall establish:</p><ol><li><p>Standards that (i) govern the use of physical force, mechanical restraints, and spit guards and (ii) avoid the use of isolation;</p></li><li><p>Staff training requirements regarding cognitive behavioral interventions, trauma-informed care, cultural background implications, de-escalation techniques, and the use of physical and mechanical restraints;</p></li><li><p>Requirements for an appropriate number of bilingual staff and culturally relevant programs;</p></li><li><p>Methods to ensure that such youth detained understand their rights and responsibilities;</p></li><li><p>Standards to ensure the provision of necessary physical and mental health care;</p></li><li><p>A requirement that any contract entered into by a juvenile correctional facility with the federal government to house youth provide staff of the Department with the same level of access to such youth that the Department would ordinarily have regarding any other youth committed to such facility; and</p></li><li><p>Standards for recordkeeping, including extended recordkeeping requirements for records and video footage related to reported incidents.</p></li></ol><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0599" target="_blank" rel="noreferrer">599</a>.</p>',4),o=[n];function l(s,c,d,h,u,p){return t(),a("div",null,o)}const m=e(r,[["render",l]]);export{g as __pageData,m as default};
