import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 66-25.2 Notice to be given prior to release of serious offenders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/66-25.2.md","filePath":"vacode/66-25.2.md"}'),r={name:"vacode/66-25.2.md"},a=i('<h1 id="§-66-25-2-notice-to-be-given-prior-to-release-of-serious-offenders" tabindex="-1">§ 66-25.2 Notice to be given prior to release of serious offenders. <a class="header-anchor" href="#§-66-25-2-notice-to-be-given-prior-to-release-of-serious-offenders" aria-label="Permalink to &quot;§ 66-25.2 Notice to be given prior to release of serious offenders.&quot;">​</a></h1><p>Prior to the release of any juvenile committed pursuant to § <a href="http://law.lis.virginia.gov/vacode/16.1-285.1/" target="_blank" rel="noreferrer">16.1-285.1</a>, the Department shall have notice of the release delivered by first-class mail to the court which committed the juvenile, to the last known address of any victim of the offense for which the juvenile was committed if such victim has submitted a written request for notification to the Department, and to the sheriff, chief of police, and attorney for the Commonwealth of the jurisdiction (i) in which the offense occurred, (ii) in which the juvenile resided prior to commitment, and (iii) if different from (i) and (ii), in which the juvenile intends to reside subsequent to being released.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0859" target="_blank" rel="noreferrer">859</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0949" target="_blank" rel="noreferrer">949</a>.</p>',3),n=[a];function s(c,f,h,l,d,_){return t(),o("div",null,n)}const m=e(r,[["render",s]]);export{u as __pageData,m as default};
