import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 66-25.3 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/66-25.3.md","filePath":"vacode/66-25.3.md"}'),n={name:"vacode/66-25.3.md"},r=o('<h1 id="§-66-25-3-definitions" tabindex="-1">§ 66-25.3 Definitions. <a class="header-anchor" href="#§-66-25-3-definitions" aria-label="Permalink to &quot;§ 66-25.3 Definitions.&quot;">​</a></h1><p>As used in this chapter unless the context requires otherwise or it is otherwise provided:</p><p>&quot;Correctional services&quot; means the following functions, services and activities when provided within a juvenile correctional facility or otherwise:</p><ol><li><p>Operation of facilities, including management, custody of juveniles and provision of security;</p></li><li><p>Food services, commissary, medical services, transportation, sanitation or other ancillary services;</p></li><li><p>Development and implementation assistance for classification, management information systems or other information systems or services;</p></li><li><p>Education, training and employment programs;</p></li><li><p>Recreational, religious and other activities; and</p></li><li><p>Counseling, special treatment programs, or other programs for special needs.</p></li></ol><p>&quot;Juvenile correction facility&quot; or &quot;center&quot; or &quot;facility&quot; means any institution operated by or under the authority of the Department and shall include, whether obtained by purchase, lease, lease/purchase, construction, reconstruction, restoration, improvement, alteration, repair or other means, any physical betterment or improvement related to the housing of juveniles or any preliminary plans, studies or surveys relative thereto; land or rights to land; and any furnishings, machines, vehicles, apparatus, or equipment for use in connection with any juvenile correctional facility.</p><p>&quot;Contractor&quot; means any entity entering into or offering or proposing to enter into a contractual agreement to provide any juvenile correctional facility for or correctional services to juveniles under the custody of the Commonwealth.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0795" target="_blank" rel="noreferrer">795</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0942" target="_blank" rel="noreferrer">942</a>.</p>',7),a=[r];function s(c,l,p,u,d,h){return i(),t("div",null,a)}const v=e(n,[["render",s]]);export{f as __pageData,v as default};
