import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 66-25.5 Powers and duties not delegable to contractor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/66-25.5.md","filePath":"vacode/66-25.5.md"}'),r={name:"vacode/66-25.5.md"},a=i('<h1 id="§-66-25-5-powers-and-duties-not-delegable-to-contractor" tabindex="-1">§ 66-25.5 Powers and duties not delegable to contractor. <a class="header-anchor" href="#§-66-25-5-powers-and-duties-not-delegable-to-contractor" aria-label="Permalink to &quot;§ 66-25.5 Powers and duties not delegable to contractor.&quot;">​</a></h1><p>No contract for juvenile correctional services shall authorize, allow, or imply a delegation of authority or responsibility of the Director to a contractor for any of the following:</p><ol><li><p>Developing and implementing procedures for calculating release and parole eligibility dates for committed juveniles;</p></li><li><p>Approving juveniles for furlough and work release;</p></li><li><p>Approving the type of work juveniles may perform and the wages which may be given the juveniles engaging in such work;</p></li><li><p>Classifying a committed juvenile or placing a committed juvenile in less restrictive custody or more restrictive custody;</p></li><li><p>Transferring a committed juvenile; however, the contractor may make written recommendations regarding the transfer of a committed juvenile;</p></li><li><p>Formulating rules of behavior for committed juveniles, violations of which may subject committed juveniles to sanctions; however, the contractor may propose such rules to the Director for his review and adoption, rejection, or modification as otherwise provided by law or regulation; and</p></li><li><p>Disciplining committed juveniles in any manner which requires a discretionary application of rules of behavior for committed juveniles or a discretionary imposition of a sanction for violations of such rules.</p></li></ol><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0795" target="_blank" rel="noreferrer">795</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0942" target="_blank" rel="noreferrer">942</a>.</p>',4),n=[a];function l(s,c,d,p,u,m){return o(),t("div",null,n)}const g=e(r,[["render",l]]);export{f as __pageData,g as default};
