import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 66-25.6 Board shall promulgate regulations; local school board exemption.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/66-25.6.md","filePath":"vacode/66-25.6.md"}'),r={name:"vacode/66-25.6.md"},l=t('<h1 id="§-66-25-6-board-shall-promulgate-regulations-local-school-board-exemption" tabindex="-1">§ 66-25.6 Board shall promulgate regulations; local school board exemption. <a class="header-anchor" href="#§-66-25-6-board-shall-promulgate-regulations-local-school-board-exemption" aria-label="Permalink to &quot;§ 66-25.6 Board shall promulgate regulations; local school board exemption.&quot;">​</a></h1><p>A. The Board shall make, adopt and promulgate regulations governing the following aspects of private management and operation of juvenile correctional facilities:</p><ol><li><p>Contingency plans for state operation of a contractor-operated facility in the event of a termination of the contract;</p></li><li><p>Use of physical force and mechanical restraint by the contractors&#39; security personnel;</p></li><li><p>Methods of monitoring a contractor-operated facility by the Department or the Board;</p></li><li><p>Public access to a contractor-operated facility; and</p></li><li><p>Such other regulations as may be necessary to carry out the provisions of this chapter.</p></li></ol><p>B. Nothing in this chapter shall be construed to require local school boards to provide educational services to juveniles while committed to a state juvenile correctional facility.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0795" target="_blank" rel="noreferrer">795</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0942" target="_blank" rel="noreferrer">942</a>.</p>',5),i=[l];function n(c,s,p,h,d,u){return o(),a("div",null,i)}const m=e(r,[["render",n]]);export{g as __pageData,m as default};
