import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 67-100 (Repealed effective October 1, 2021) Legislative findings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/67-100.md","filePath":"vacode/67-100.md"}'),n={name:"vacode/67-100.md"},o=i('<h1 id="§-67-100-repealed-effective-october-1-2021-legislative-findings" tabindex="-1">§ 67-100 (Repealed effective October 1, 2021) Legislative findings. <a class="header-anchor" href="#§-67-100-repealed-effective-october-1-2021-legislative-findings" aria-label="Permalink to &quot;§ 67-100 (Repealed effective October 1, 2021) Legislative findings.&quot;">​</a></h1><p>The General Assembly hereby finds that:</p><ol><li><p>Energy is essential to the health, safety, and welfare of the people of this Commonwealth and to the Commonwealth&#39;s economy;</p></li><li><p>The state government should facilitate the availability and delivery of reliable and adequate supplies of energy to industrial, commercial, and residential users at reasonable costs such that these users and the Commonwealth&#39;s economy are able to be productive;</p></li><li><p>The Commonwealth would benefit from articulating clear objectives pertaining to energy issues, adopting an energy policy that advances these objectives, and establishing a procedure for measuring the implementation of these policies;</p></li><li><p>Climate change is an urgent and pressing challenge for Virginia. Swift decarbonization and a transition to clean energy are required to meet the urgency of the challenge; and</p></li><li><p>The Commonwealth will benefit from being a leader in deploying a low-carbon energy economy.</p></li></ol><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1191" target="_blank" rel="noreferrer">1191</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1192" target="_blank" rel="noreferrer">1192</a>.</p>',4),r=[o];function l(s,c,d,f,h,p){return a(),t("div",null,r)}const _=e(n,[["render",l]]);export{m as __pageData,_ as default};
