import{_ as a,o,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 67-1201 (Repealed effective October 1, 2021) Authority created; purpose.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/67-1201.md","filePath":"vacode/67-1201.md"}'),r={name:"vacode/67-1201.md"},n=e("h1",{id:"§-67-1201-repealed-effective-october-1-2021-authority-created-purpose",tabindex:"-1"},[t("§ 67-1201 (Repealed effective October 1, 2021) Authority created; purpose. "),e("a",{class:"header-anchor",href:"#§-67-1201-repealed-effective-october-1-2021-authority-created-purpose","aria-label":'Permalink to "§ 67-1201 (Repealed effective October 1, 2021) Authority created; purpose."'},"​")],-1),s=e("p",null,"The Virginia Offshore Wind Development Authority is created as a body corporate and a political subdivision of the Commonwealth and as such shall have, and is vested with, all of the politic and corporate powers as are set forth in this chapter. The Authority is established for the purposes of facilitating, coordinating, and supporting the development, either by the Authority or by other qualified entities, of the offshore wind energy industry, offshore wind energy projects, and associated supply chain vendors by collecting relevant metocean and environmental data, by identifying existing state and regulatory or administrative barriers to the development of the offshore wind energy industry, by working in cooperation with relevant local, state, and federal agencies to upgrade port and other logistical facilities and sites to accommodate the manufacturing and assembly of offshore wind energy project components and vessels, and by ensuring that the development of such projects is compatible with other ocean uses and avian and marine resources, including both the possible interference with and positive effects on naval facilities and operations, NASA-Wallops Flight Facility operations, shipping lanes, recreational and commercial fisheries, and avian and marine species and habitats. The Authority shall, in cooperation with the relevant state and federal agencies as necessary, recommend ways to encourage and expedite the development of the offshore wind energy industry. The Authority shall also consult with research institutions, businesses, nonprofit organizations, and stakeholders as the Authority deems appropriate.",-1),c=e("p",null,"The Authority shall have only those powers enumerated in this chapter.",-1),d=e("p",null,[t("2010, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0507",target:"_blank",rel:"noreferrer"},"507"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0681",target:"_blank",rel:"noreferrer"},"681"),t(".")],-1),h=[n,s,c,d];function l(p,f,u,g,y,m){return o(),i("div",null,h)}const _=a(r,[["render",l]]);export{b as __pageData,_ as default};
