import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 67-1207 (Repealed effective October 1, 2021) Powers and duties of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/67-1207.md","filePath":"vacode/67-1207.md"}'),r={name:"vacode/67-1207.md"},i=o('<h1 id="§-67-1207-repealed-effective-october-1-2021-powers-and-duties-of-the-authority" tabindex="-1">§ 67-1207 (Repealed effective October 1, 2021) Powers and duties of the Authority. <a class="header-anchor" href="#§-67-1207-repealed-effective-october-1-2021-powers-and-duties-of-the-authority" aria-label="Permalink to &quot;§ 67-1207 (Repealed effective October 1, 2021) Powers and duties of the Authority.&quot;">​</a></h1><p>In addition to such other powers and duties established under this chapter, the Authority shall have the power and duty to:</p><ol><li><p>Adopt, use, and alter at will an official seal;</p></li><li><p>Make bylaws for the management and regulation of its affairs;</p></li><li><p>Maintain an office at such place or places within the Commonwealth as it may designate;</p></li><li><p>Accept, hold, and administer moneys, grants, securities, or other property transferred, given, or bequeathed to the Authority, absolutely or in trust, from any source, public or private, for the purposes for which the Authority is created;</p></li><li><p>Make and execute contracts and all other instruments and agreements necessary or convenient for the exercise of its powers and functions;</p></li><li><p>Employ, in its discretion, consultants, attorneys, architects, engineers, accountants, financial experts, investment bankers, superintendents, managers, and such other employees and agents as may be necessary, and fix their compensation to be payable from funds made available to the Authority;</p></li><li><p>Invest its funds as permitted by applicable law;</p></li><li><p>Receive and accept from any federal or private agency, foundation, corporation, association, or person grants, donations of money, or real or personal property for the benefit of the Authority, and receive and accept from the Commonwealth or any state, and from any municipality, county, or other political subdivision thereof and any other source, aid or contributions of either money, property, or other things of value, to be held, used, and applied for the purposes for which such grants and contributions may be made;</p></li><li><p>Enter into agreements with any department, agency, or instrumentality of the United States or of the Commonwealth and with lenders and enter into loans with contracting parties for the purpose of planning, regulating, and providing for the financing or assisting in the financing of any project;</p></li><li><p>Do any lawful act necessary or appropriate to carry out the powers herein granted or reasonably implied;</p></li><li><p>Identify and take steps to mitigate existing state and regulatory or administrative barriers to the development of the offshore wind energy industry, including facilitating any permitting processes; and</p></li><li><p>Enter into interstate partnerships to develop the offshore wind energy industry and offshore wind energy projects.</p></li></ol><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>.</p>',4),n=[i];function s(p,l,d,c,h,f){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{y as __pageData,m as default};
