import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 67-1208 (Repealed effective October 1, 2021) Director; staff; counsel to the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/67-1208.md","filePath":"vacode/67-1208.md"}'),a={name:"vacode/67-1208.md"},i=o('<h1 id="§-67-1208-repealed-effective-october-1-2021-director-staff-counsel-to-the-authority" tabindex="-1">§ 67-1208 (Repealed effective October 1, 2021) Director; staff; counsel to the Authority. <a class="header-anchor" href="#§-67-1208-repealed-effective-october-1-2021-director-staff-counsel-to-the-authority" aria-label="Permalink to &quot;§ 67-1208 (Repealed effective October 1, 2021) Director; staff; counsel to the Authority.&quot;">​</a></h1><p>A. The Director of the Department of Mines, Minerals and Energy shall serve as Director of the Authority and shall administer the affairs and business of the Authority in accordance with the provisions of this chapter and subject to the policies, control, and direction of the Authority. The Director shall maintain, and be custodian of, all books, documents, and papers of or filed with the Authority. The Director may cause copies to be made of all minutes and other records and documents of the Authority and may give certificates under seal of the Authority to the effect that such copies are true copies, and all persons dealing with the Authority may rely on such certificates. The Director also shall perform such other duties as prescribed by the Authority in carrying out the purposes of this chapter.</p><p>B. The Division of Offshore Wind within the Department of Mines, Minerals and Energy shall serve as staff to the Authority.</p><p>C. The Office of the Attorney General shall provide counsel to the Authority.</p><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),s=[i];function c(n,h,f,l,d,p){return t(),r("div",null,s)}const y=e(a,[["render",c]]);export{_ as __pageData,y as default};
