import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 67-1700 (Repealed effective October 1, 2021) Nuclear energy; strategic plan.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/67-1700.md","filePath":"vacode/67-1700.md"}'),o={name:"vacode/67-1700.md"},n=r('<h1 id="§-67-1700-repealed-effective-october-1-2021-nuclear-energy-strategic-plan" tabindex="-1">§ 67-1700 (Repealed effective October 1, 2021) Nuclear energy; strategic plan. <a class="header-anchor" href="#§-67-1700-repealed-effective-october-1-2021-nuclear-energy-strategic-plan" aria-label="Permalink to &quot;§ 67-1700 (Repealed effective October 1, 2021) Nuclear energy; strategic plan.&quot;">​</a></h1><p>A. The Department and the Secretaries of Commerce and Trade and Education shall work in coordination with the Virginia Nuclear Energy Consortium Authority (VNECA), established pursuant to Chapter 14 (§ <a href="/vacode/67-1400/">67-1400</a> et seq.), and the Virginia Economic Development Partnership Authority, established pursuant to Article 4 (§ <a href="/vacode/2.2-2234/">2.2-2234</a> et seq.) of Chapter 22 of Title 2.2, to develop a strategic plan for nuclear energy as part of the Commonwealth&#39;s overall goal of carbon-free energy.</p><p>B. Such plan may include (i) the promotion of new technologies and opportunities for innovation, including advanced manufacturing; (ii) the establishment of a collaborative research center and university nuclear leadership program to promote education in fields that meet the workforce demands of Virginia&#39;s nuclear industry; and (iii) recognition of the role of nuclear energy in the Commonwealth&#39;s goal of employing 100 percent carbon-free sources of energy by 2050.</p><p>C. Such plan shall be completed by October 1, 2020, shall be updated every four years thereafter, and shall be published on the Internet by VNECA.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0658" target="_blank" rel="noreferrer">658</a>.</p>',5),i=[n];function c(l,s,p,d,h,f){return a(),t("div",null,i)}const m=e(o,[["render",c]]);export{g as __pageData,m as default};
