import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 8.01-2 General definitions for this title.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-2.md","filePath":"vacode/8.01-2.md"}'),a={name:"vacode/8.01-2.md"},n=o('<h1 id="§-8-01-2-general-definitions-for-this-title" tabindex="-1">§ 8.01-2 General definitions for this title. <a class="header-anchor" href="#§-8-01-2-general-definitions-for-this-title" aria-label="Permalink to &quot;§ 8.01-2 General definitions for this title.&quot;">​</a></h1><p>As used in this title, unless the context otherwise requires, the term:</p><ol><li><p>&quot;Action&quot; and &quot;suit&quot; may be used interchangeably and shall include all civil proceedings whether upon claims at law, in equity, or statutory in nature and whether in circuit courts or district courts;</p></li><li><p>&quot;Decree&quot; and &quot;judgment&quot; may be used interchangeably and shall include orders or awards;</p></li><li><p>&quot;Fiduciary&quot; shall include any one or more of the following:</p></li></ol><p>a. guardian,</p><p>b. committee,</p><p>c. trustee,</p><p>d. executor,</p><p>e. administrator, and administrator with the will annexed,</p><p>f. curator of the will of any decedent, or</p><p>g. conservator;</p><ol start="4"><li><p>&quot;Rendition of a judgment&quot; means the time at which the judgment is signed and dated;</p></li><li><p>&quot;Person&quot; shall include individuals, a trust, an estate, a partnership, an association, an order, a corporation, or any other legal or commercial entity;</p></li><li><p>&quot;Person under a disability&quot; shall include:</p></li></ol><p>a. a person convicted of a felony during the period he is confined;</p><p>b. an infant;</p><p>c. an incapacitated person as defined in § <a href="/vacode/64.2-2000/">64.2-2000</a>;</p><p>d. an incapacitated ex-service person under § <a href="/vacode/64.2-2016/">64.2-2016</a>;</p><p>e. persons made defendants by the general description of &quot;parties unknown&quot; in suits involving real property; or</p><p>f. any other person who, upon motion to the court by any party to an action or suit or by any person in interest, is determined to be (i) incapable of taking proper care of his person, or (ii) incapable of properly handling and managing his estate, or (iii) otherwise unable to defend his property or legal rights either because of age or temporary or permanent impairment, whether physical, mental, or both. Such impairment may also include substance abuse as defined in § <a href="/vacode/37.2-100/">37.2-100</a>;</p><ol start="7"><li><p>&quot;Sheriff&quot; shall include deputy sheriffs and such other persons designated in § <a href="/vacode/15.2-1603/">15.2-1603</a>;</p></li><li><p>&quot;Summons&quot; and &quot;subpoena&quot; may be used interchangeably and shall include a subpoena duces tecum for the production of documents and tangible things;</p></li><li><p>&quot;Court of equity,&quot;&quot;law and equity court,&quot;&quot;law and chancery court,&quot;&quot;chancery court,&quot; &quot;corporation court,&quot; &quot;the chancery side,&quot; &quot;court exercising powers in chancery,&quot; &quot;court with equitable jurisdiction,&quot; and &quot;receivership court&quot; shall mean the circuit court when entertaining equitable claims;</p></li><li><p>A &quot;motion for judgment,&quot; &quot;bill,&quot; &quot;bill of complaint,&quot; or &quot;bill in equity&quot; shall mean a complaint in a civil action, as provided in the Rules of Supreme Court of Virginia;</p></li><li><p>&quot;Equity practice,&quot; &quot;equity procedure,&quot; &quot;chancery practice,&quot; and &quot;chancery procedure&quot; shall mean practice and procedure in a civil action as prescribed by this Code and the Rules of Supreme Court of Virginia.</p></li></ol><p>1977, c. 617; 1988, c. 37; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0921" target="_blank" rel="noreferrer">921</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0299" target="_blank" rel="noreferrer">299</a>.</p>',19),r=[n];function l(u,s,c,p,d,h){return t(),i("div",null,r)}const g=e(a,[["render",l]]);export{f as __pageData,g as default};
