import{_ as e,o as i,c as n,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.01-216.10 Civil investigative demands; issuance; sharing information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-216.10.md","filePath":"vacode/8.01-216.10.md"}'),t={name:"vacode/8.01-216.10.md"},r=a('<h1 id="§-8-01-216-10-civil-investigative-demands-issuance-sharing-information" tabindex="-1">§ 8.01-216.10 Civil investigative demands; issuance; sharing information. <a class="header-anchor" href="#§-8-01-216-10-civil-investigative-demands-issuance-sharing-information" aria-label="Permalink to &quot;§ 8.01-216.10 Civil investigative demands; issuance; sharing information.&quot;">​</a></h1><p>A. Whenever the Attorney General or his designee has reason to believe that any person may be in possession, custody, or control of any documentary material or information relevant to a false claims law investigation, the Attorney General or his designee may, before commencing a civil proceeding or making an election under this article, issue in writing and cause to be served upon such person, a civil investigative demand requiring such person (i) to produce such documentary material for inspection and copying, (ii) to answer in writing written interrogatories with respect to such documentary material or information, (iii) to give oral testimony concerning such documentary material or information, or (iv) to furnish any combination of such material, answers, or testimony.</p><p>B. Whenever a civil investigative demand is an express demand for any product of discovery, the Attorney General shall cause to be served, in any manner authorized by this article, a copy of such demand upon the person from whom the discovery was obtained and shall notify the person to whom such demand is issued of the date on which such copy was served.</p><p>C. Any information obtained by the Attorney General or his designee pursuant to this section may be shared with any qui tam relator and any state or federal governmental entity if the Attorney General or his designee determines that such information is necessary as part of any false claims investigation.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0479" target="_blank" rel="noreferrer">479</a>.</p>',5),o=[r];function s(c,d,l,h,m,v){return i(),n("div",null,o)}const f=e(t,[["render",s]]);export{u as __pageData,f as default};
