import{_ as t,o as i,c as n,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 8.01-216.14 Civil investigative demands; documentary material.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-216.14.md","filePath":"vacode/8.01-216.14.md"}'),r={name:"vacode/8.01-216.14.md"},o=e("h1",{id:"§-8-01-216-14-civil-investigative-demands-documentary-material",tabindex:"-1"},[a("§ 8.01-216.14 Civil investigative demands; documentary material. "),e("a",{class:"header-anchor",href:"#§-8-01-216-14-civil-investigative-demands-documentary-material","aria-label":'Permalink to "§ 8.01-216.14 Civil investigative demands; documentary material."'},"​")],-1),s=e("p",null,"The production of documentary material in response to a civil investigative demand served under this article shall be made under a sworn certificate, in such form as the demand designates, by (i) in the case of a natural person, the person to whom the demand is directed, or (ii) in the case of a person other than a natural person, a person having knowledge of the facts and circumstances relating to such production and authorized to act on behalf of such person. The certificate shall state that all of the documentary material required by the demand and in the possession, custody, or control of the person to whom the demand is directed has been produced and made available to the investigator identified in the demand.",-1),d=e("p",null,"Any person upon whom any civil investigative demand for the production of documentary material has been served shall make such material available for inspection and copying to the investigator identified in such demand at the principal place of business of such person, or at such other place as the investigator and the person thereafter may agree and prescribe in writing, or as the court may direct. Such material shall be made available on the return date specified in such demand, or on such later date as the investigator may prescribe in writing. Such person may, upon written agreement between the person and the investigator, substitute copies for originals of all or any part of such material.",-1),c=e("p",null,[a("2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0842",target:"_blank",rel:"noreferrer"},"842"),a(".")],-1),l=[o,s,d,c];function h(m,u,p,v,f,g){return i(),n("div",null,l)}const y=t(r,[["render",h]]);export{b as __pageData,y as default};
