import{_ as i,o as a,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-216.15 Civil investigative demands; interrogatories.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-216.15.md","filePath":"vacode/8.01-216.15.md"}'),r={name:"vacode/8.01-216.15.md"},s=e("h1",{id:"§-8-01-216-15-civil-investigative-demands-interrogatories",tabindex:"-1"},[t("§ 8.01-216.15 Civil investigative demands; interrogatories. "),e("a",{class:"header-anchor",href:"#§-8-01-216-15-civil-investigative-demands-interrogatories","aria-label":'Permalink to "§ 8.01-216.15 Civil investigative demands; interrogatories."'},"​")],-1),o=e("p",null,"Each inquiry in a civil investigative demand served under this article shall be answered separately and fully in writing under oath and shall be submitted under a sworn certificate, in such form as the demand designates, by (i) in the case of a natural person, the person to whom the demand is directed, or (ii) in the case of a person other than a natural person, the person or persons responsible for answering each inquiry. If any inquiry is objected to, the reasons for the objection shall be stated in the certificate instead of an answer. The certificate shall state that all information required by the demand and in the possession, custody, control, or knowledge of the person to whom the demand is directed has been submitted. To the extent that any information is not furnished, the information shall be identified and reasons set forth with particularity regarding the reasons why the information was not furnished.",-1),d=e("p",null,[t("2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0842",target:"_blank",rel:"noreferrer"},"842"),t(".")],-1),h=[s,o,d];function c(l,f,m,p,v,u){return a(),n("div",null,h)}const b=i(r,[["render",c]]);export{_ as __pageData,b as default};
