import{_ as t,o,c as a,z as e,a as s}from"./chunks/framework.9482e208.js";const w=JSON.parse(`{"title":"§ 8.01-216 Comptroller's power to adjust old claims.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-216.md","filePath":"vacode/8.01-216.md"}`),n={name:"vacode/8.01-216.md"},r=e("h1",{id:"§-8-01-216-comptroller-s-power-to-adjust-old-claims",tabindex:"-1"},[s("§ 8.01-216 Comptroller's power to adjust old claims. "),e("a",{class:"header-anchor",href:"#§-8-01-216-comptroller-s-power-to-adjust-old-claims","aria-label":`Permalink to "§ 8.01-216 Comptroller's power to adjust old claims."`},"​")],-1),i=e("p",null,"The Comptroller, with the advice of the Attorney General, may adjust and settle upon equitable principles, without regard to strict legal rules, any doubtful or disputed account or claim in favor of the Commonwealth which may have been standing on the books of his office not less than two years, and may, with the like advice, dismiss any proceedings instituted by him; but before such adjustment or settlement can in any wise affect the rights of the Commonwealth it shall be approved and endorsed by the Attorney General and shall then be submitted to the supervision of the judge of the Circuit Court of the City of Richmond, accompanied by a written statement signed by the Comptroller of the facts and reasons which, in his opinion, render such adjustment or settlement just and proper. When such judge endorses the same with his written approval, signed in his official character, it shall be considered and treated as valid and binding.",-1),d=e("p",null,"Code 1950, § 8-779; 1977, c. 617.",-1),l=[r,i,d];function c(h,m,p,u,f,_){return o(),a("div",null,l)}const y=t(n,[["render",c]]);export{w as __pageData,y as default};
