import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.01-220.1:2 Civil immunity for teachers under certain circumstances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-220.1:2.md","filePath":"vacode/8.01-220.1:2.md"}'),t={name:"vacode/8.01-220.1:2.md"},o=a('<h1 id="§-8-01-220-1-2-civil-immunity-for-teachers-under-certain-circumstances" tabindex="-1">§ 8.01-220.1:2 Civil immunity for teachers under certain circumstances. <a class="header-anchor" href="#§-8-01-220-1-2-civil-immunity-for-teachers-under-certain-circumstances" aria-label="Permalink to &quot;§ 8.01-220.1:2 Civil immunity for teachers under certain circumstances.&quot;">​</a></h1><p>A. Any teacher employed by a local school board in the Commonwealth shall not be liable for any civil damages for any acts or omissions resulting from the supervision, care or discipline of students when such acts or omissions are within such teacher&#39;s scope of employment and are taken in good faith in the course of supervision, care or discipline of students, unless such acts or omissions were the result of gross negligence or willful misconduct.</p><p>B. No school employee or school volunteer shall be liable for any civil damages arising from the prompt good faith reporting of alleged acts of bullying or crimes against others to the appropriate school official in compliance with §§ <a href="http://law.lis.virginia.gov/vacode/22.1-279.6/" target="_blank" rel="noreferrer">22.1-279.6</a> and <a href="http://law.lis.virginia.gov/vacode/22.1-291.4/" target="_blank" rel="noreferrer">22.1-291.4</a> and specified procedures.</p><p>C. This section shall not be construed to limit, withdraw, or overturn any defense or immunity already existing in statutory or common law, to affect any claim occurring prior to the effective date of this law, or to prohibit any person subject to bullying or a criminal act from seeking redress under any other provision of law.</p><p>1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0349" target="_blank" rel="noreferrer">349</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0879" target="_blank" rel="noreferrer">879</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0462" target="_blank" rel="noreferrer">462</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0575" target="_blank" rel="noreferrer">575</a>.</p>',5),n=[o];function s(c,l,h,f,d,m){return r(),i("div",null,n)}const g=e(t,[["render",s]]);export{u as __pageData,g as default};
